package all

import (
	"github.com/bluenviron/gomavlib/v3/pkg/dialects/marsh"
)

// Mode of a motion platform system.
type MOTION_PLATFORM_MODE = marsh.MOTION_PLATFORM_MODE

const (
	// Mode information is unsupported on this device.
	MOTION_PLATFORM_MODE_UNKNOWN MOTION_PLATFORM_MODE = marsh.MOTION_PLATFORM_MODE_UNKNOWN
	// Mode is currently not available, but may be in different condition.
	MOTION_PLATFORM_MODE_UNINITIALIZED MOTION_PLATFORM_MODE = marsh.MOTION_PLATFORM_MODE_UNINITIALIZED
	// Platform actuators are turned off, but control system is responsive.
	MOTION_PLATFORM_MODE_OFF MOTION_PLATFORM_MODE = marsh.MOTION_PLATFORM_MODE_OFF
	// Platform is in the lowest position and/or locked, appropriate for personnel entry.
	MOTION_PLATFORM_MODE_SETTLED MOTION_PLATFORM_MODE = marsh.MOTION_PLATFORM_MODE_SETTLED
	// Platform is in a neutral reference position, not accepting movement commands.
	MOTION_PLATFORM_MODE_NEUTRAL MOTION_PLATFORM_MODE = marsh.MOTION_PLATFORM_MODE_NEUTRAL
	// Platform is stopped in any position, not accepting movement commands.
	MOTION_PLATFORM_MODE_FROZEN MOTION_PLATFORM_MODE = marsh.MOTION_PLATFORM_MODE_FROZEN
	// Platform is in any position, accepting movement commands.
	MOTION_PLATFORM_MODE_ENGAGED MOTION_PLATFORM_MODE = marsh.MOTION_PLATFORM_MODE_ENGAGED
)
