package all

import (
	"github.com/bluenviron/gomavlib/v3/pkg/dialects/development"
)

// The frame of a target observation from an onboard sensor.
type TARGET_OBS_FRAME = development.TARGET_OBS_FRAME

const (
	// NED local tangent frame (x: North, y: East, z: Down) with origin fixed relative to earth.
	TARGET_OBS_FRAME_LOCAL_NED TARGET_OBS_FRAME = development.TARGET_OBS_FRAME_LOCAL_NED
	// FRD local frame aligned to the vehicle's attitude (x: Forward, y: Right, z: Down) with an origin that travels with vehicle.
	TARGET_OBS_FRAME_BODY_FRD TARGET_OBS_FRAME = development.TARGET_OBS_FRAME_BODY_FRD
	// NED local tangent frame (x: North, y: East, z: Down) with an origin that travels with vehicle.
	TARGET_OBS_FRAME_LOCAL_OFFSET_NED TARGET_OBS_FRAME = development.TARGET_OBS_FRAME_LOCAL_OFFSET_NED
	// Other sensor frame for target observations neither in local NED nor in body FRD.
	TARGET_OBS_FRAME_OTHER TARGET_OBS_FRAME = development.TARGET_OBS_FRAME_OTHER
)
