//autogenerated:yes
//nolint:revive,govet,errcheck
package common

import (
	"testing"

	"github.com/stretchr/testify/require"
)

func TestEnum_REBOOT_SHUTDOWN_CONDITIONS(t *testing.T) {
	t.Run("zero", func(t *testing.T) {
		var e REBOOT_SHUTDOWN_CONDITIONS
		e.UnmarshalText([]byte{})
		e.MarshalText()
		e.String()
	})

	t.Run("first entry", func(t *testing.T) {
		enc, err := REBOOT_SHUTDOWN_CONDITIONS_SAFETY_INTERLOCKED.MarshalText()
		require.NoError(t, err)

		var dec REBOOT_SHUTDOWN_CONDITIONS
		err = dec.UnmarshalText(enc)
		require.NoError(t, err)

		require.Equal(t, REBOOT_SHUTDOWN_CONDITIONS_SAFETY_INTERLOCKED, dec)
	})
}
