package pythonarraytest

import (
	"github.com/bluenviron/gomavlib/v3/pkg/dialects/common"
)

// Reboot/shutdown action for selected component in MAV_CMD_PREFLIGHT_REBOOT_SHUTDOWN.
type REBOOT_SHUTDOWN_ACTION = common.REBOOT_SHUTDOWN_ACTION

const (
	// Do nothing.
	REBOOT_SHUTDOWN_ACTION_NONE REBOOT_SHUTDOWN_ACTION = common.REBOOT_SHUTDOWN_ACTION_NONE
	// Reboot component.
	REBOOT_SHUTDOWN_ACTION_REBOOT REBOOT_SHUTDOWN_ACTION = common.REBOOT_SHUTDOWN_ACTION_REBOOT
	// Shutdown component.
	REBOOT_SHUTDOWN_ACTION_SHUTDOWN REBOOT_SHUTDOWN_ACTION = common.REBOOT_SHUTDOWN_ACTION_SHUTDOWN
	// Reboot component and keep it in the bootloader until upgraded.
	REBOOT_SHUTDOWN_ACTION_REBOOT_TO_BOOTLOADER REBOOT_SHUTDOWN_ACTION = common.REBOOT_SHUTDOWN_ACTION_REBOOT_TO_BOOTLOADER
	// Power on component. Do nothing if component is already powered (ACK command with MAV_RESULT_ACCEPTED).
	REBOOT_SHUTDOWN_ACTION_POWER_ON REBOOT_SHUTDOWN_ACTION = common.REBOOT_SHUTDOWN_ACTION_POWER_ON
)
